sp_widget.client_script:
function() {

  var c = this;

	c.selectItem = function(idx) {

		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);

		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updateNoteList', function(event, data) {

		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {

			var len = c.data.notes.length;
			for (var i = 0; i < len; i++) {

				var noteToUpdate = c.data.notes[i];
				if (noteToUpdate.sys_id == $rootScope.noteID) {

					var responseNote = r.data.notes[i];
					noteToUpdate.title = responseNote.title;
					noteToUpdate.note = responseNote.note;
					break;
				}
			}			
		});	
	});
}

sp_widget.script:
(function() {

	data.notes = [];

	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {

		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		noteObj.note = noteGR.getValue('note').slice(0, 20);
		data.notes.push(noteObj);
	}
})();
